# Function to generate random engagement rate and posts engaged
def generate_random_data():
    posts_engaged = round(random.uniform(5.0, 25.0), 1)
    connections = random.randint(100, 2000)
    engagement_rate = round(random.uniform(0.1, 0.6), 2)
    recent_activity = random.choice(activity_statuses)
    return posts_engaged, connections, engagement_rate, recent_activity

# Generate large amounts of dummy data
data = []
for lead_id in range(1, 5001):  # 1000 rows
    first_name = random.choice(first_names)
    last_name = random.choice(last_names)
    full_name = f"{first_name} {last_name}"  # Combine first and last names
    role = random.choice(roles)
    industry = random.choice(industries)
    posts_engaged, connections, engagement_rate, recent_activity = generate_random_data()
